package demo;

public class operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=2;
		int c=5;
		int d=8;
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);//arithmetic operators
		System.out.println(" ");
		System.out.println(a>b);
		System.out.println(a<b);
		System.out.println(a<=b);
		System.out.println(a>=b);
		System.out.println(a==b);
		System.out.println(a!=b);
		System.out.println(" ");//comparision operators
		System.out.println(a&b);
		System.out.println(a|b);
		System.out.println(a^b);
		System.out.println(~b);
		System.out.println(" ");//bitwise operators
		System.out.println(a<<b);//a*2^b left shift
		System.out.println(a>>b);//a/2^b right shift
		System.out.println(" "); 
		System.out.println(a>b&&c<d);
		System.out.println(a<b||c>d);
		System.out.println(!(a>b));//logical operators
		System.out.println(" ");
		System.out.println(a+=b);
		System.out.println(a-=b);
		System.out.println(a&=b);
		System.out.println(a|=b);
		System.out.println(a/=b);//assignment
		System.out.println(" ");
		System.out.println(a>b?a:b);//ternary
		System.out.println(" ");
		System.out.println(a++);
		System.out.println(++a);
		System.out.println(a--);
		System.out.println(--a);//unary operators
		
		
		
		
		

	}

		 

	




	}


